mapper.select:
         SELECT
           t.id id,
           t.name name,
		   t.sex sex,
		   t.age age,
		   t.email email,
		   t.address address,
		   t.create_time createTime,
		   t.update_time updateTime
         FROM
            t_test t
        WHERE
         1=1
          AND t.id = #{id}
          AND t.name LIKE #{name}
          AND t.sex = #{sex}  
           AND t.age = #{age}  
          AND t.email = #{email} 
          AND t.create_time = #{createTime}  
          AND t.update_time = #{updateTime}  
          ORDER BY t.id = #{id}

        SELECT
           t.id as 'id',
           t.name as 'name',
		   t.sex as 'sex',
		   t.age as 'age',
		   t.email as 'email',
		   t.address as 'address',
		   t.create_time as 'createTime',
		   t.update_time as 'updateTime'
         FROM
           t_test t
         WHERE
         1=1
          AND t.id = #{id}
          AND t.name LIKE #{name}
          AND t.sex = #{sex}  
           AND t.age = #{age}  
          AND t.email = #{email} 
          AND t.create_time = #{createTime}  
          AND t.update_time = #{updateTime}  
          ORDER BY t.id = #{id}

mapper.delete:
DELETE FROM t_test
WHERE 
1 = 1
AND id = #{id} 
 AND name = #{name} 
 AND t.sex = #{sex}  
  AND t.age = #{age}  
 AND t.email = #{email} 
 AND t.create_time = #{createTime}  
 AND t.update_time = #{updateTime}
